mapper.update:
UPDATE lp_cargo
cargo_name = #{cargoName},
cargo_type = #{cargoType},
cargo_price = #{cargoPrice},
is_deleted = #{isDeleted}
WHERE cid = #{cid};

mapper.select:
select lpc.cid as cid, lpc.cargo_name as cargoName, lpc.cargo_price as cargoPrice, lpc.cargo_type as cargoType, lpc.is_deleted as isDeleted from lp_cargo lpc where lpc.is_deleted=0
and lpc.cargo_name = #{keywords}
and lpc.cargo_type = #{cargoType}

select lgb.bag_code as bagCode, lc.cargo_name as cargoName, lc.cargo_price as cargoPrice, lod.cargo_weight as cargoWeight, lo.order_code as orderCode, la.area_name as areaName, la.city_name as cityName, la.province_name as provinceName, la.address as address, la.addressee as addressee, la.addressee_tel as addresseeTel from lp_order lo left join lp_address la on lo.address_id=la.aid left join lp_garbage_bag lgb on lo.oid=lgb.oid left join lp_order_details lod on lgb.bag_code=lod.bag_code left join lp_cargo lc on lod.cid=lc.cid where 1=1
and lo.oid = #{oid}
and lgb.bag_code = #{bagCode}

mapper.insert:
INSERT INTO lp_order_details (
did,
bag_code,
cid,
cargo_weight
)
VALUES
(
  #{did},
  #{bagCode},
  #{cid},
  #{cargoWeight}
);
