mapper.select:
select count(open_id)
from lp_role
where 1=1

    and create_time >= #{startTime}


    and create_time <= #{endTime}

select lc.cargo_name as cargoName, sum(lod.cargo_weight) as weightSum from lp_user lu left join lp_order lo on lu.uid=lo.receive_id left join lp_garbage_bag lgb on lo.oid=lgb.oid left join lp_order_details lod on lod.bag_code=lgb.bag_code left join lp_cargo lc on lc.cid=lod.cid where lo.order_state=4
and lu.account = #{account}
and lo.order_finish_time >= #{startTime}
and lo.order_finish_time <= #{endTime}
group by lod.cid

select lc.cargo_name as cargoName, sum(lod.cargo_weight) as weightSum, sum(lo.all_price) as priceSum from lp_user lu left join lp_order lo on lu.uid=lo.check_id left join lp_garbage_bag lgb on lo.oid=lgb.oid left join lp_order_details lod on lod.bag_code=lgb.bag_code left join lp_cargo lc on lc.cid=lod.cid where lo.order_state=4
and lu.account = #{account}
and lo.order_finish_time >= #{startTime}
and lo.order_finish_time <= #{endTime}
group by lod.cid

select sum(lo.all_price)
from lp_order lo
where lo.order_state = 4 and lo.open_id=#{openId}
