mapper.select:
select lo.oid as oId, lo.order_code as orderCode, lo.open_id as openId, lo.receive_id AS receiveId, lu.user_name as userName, lu.cell_number as cellNumber, lo.admin_id AS adminId, lu2.user_name as adminName, lo.check_id as checkId, lu3.user_name as checkName, lu3.cell_number as checkNumber, lo.address_id AS addressId, la.area_name as areaName, la.city_name as cityName, la.province_name as provinceName, la.addressee as addressee, la.address as address, la.addressee_tel as addresseeTel, lo.appointment_time AS appointmentTime, lo.is_account AS isAccount, lo.price_evaluation AS priceEvaluation, lo.is_deleted AS isDeleted, lo.order_send_time AS orderSendTime, lo.order_get_time AS orderGetTime, lo.order_finish_time AS orderFinishTime, lo.create_user_id AS createUserId, lo.modify_user_id AS modifyUserId, lo.order_create_time as orderCreateTime, lo.all_price as allPrice, lo.deduct_money as deductMoney, lo.deduct_msg as deductmsg, lo.order_state as orderState, lo.sp_state as spState, lo.order_type as orderType, lo.individual_or_merchant as individualOrMerchant, GROUP_CONCAT(DISTINCT lgb.bag_code) as bagCode from lp_order lo left join lp_user lu on lo.receive_id=lu.uid left join lp_user lu2 on lo.admin_id=lu2.uid left join lp_user lu3 on lo.check_id=lu3.uid left join lp_role lr on lo.open_id=lr.open_id left join lp_address la on lo.address_id=la.aid left join lp_equipment le on le.eid=lo.eid left join lp_garbage_bag lgb on lo.oid=lgb.oid where lo.is_deleted=0
AND lo.oid = #{oid}
AND lo.is_deleted = #{isDeleted}
AND (la.addressee like "%"#{keywords}"%" or ar.name like "%"#{keywords}"%")
AND lo.order_type = #{orderType}
AND lo.receive_id = #{uid}
AND lo.open_id = #{openId}
and lo.order_state in
#{state}
AND DATE_FORMAT(lo.order_create_time,'%Y/%m/%d %H:%i:%S') >= DATE_FORMAT(#{startTime},'%Y/%m/%d %H:%i:%S')
AND DATE_FORMAT(lo.order_create_time,'%Y/%m/%d %H:%i:%S') <=DATE_FORMAT(#{endTime},'%Y/%m/%d %H:%i:%S')
group by lo.oid order by lo.order_create_time desc

mapper.insert:
		INSERT INTO lp_garbage_bag (
		bag_id,
        oid,
        bag_code
		)
		VALUES
		(
		  #{bagId},
		  #{oid},
		  #{bagCode}
		);

INSERT INTO lp_role_garbage (
open_id, bag_code, is_delete
)
VALUES
(
  #{openId},
  #{bagCode},
  0
);
