mapper.select:
select
  id,
  name,
  code
from province pro
where 1=1

    AND code = #{code}


    AND name = #{name}


    AND id = #{id}

select
id,
name,
code,
provincecode
from city
where 1=1

    AND code = #{code}


    AND name = #{name}


    AND id = #{id}


    AND provincecode = #{provinceCode}

select
id,
name,
code,
citycode
from area
where 1=1

    AND code = #{code}


    AND name = #{name}


    AND id = #{id}


    AND citycode = #{cityCode}

select lpa.aid as aid, lpa.addressee as addressee, lpa.addressee_tel as addresseeTel, lpa.address as address, pro.code as provinceCode, pro.name as provincename, cit.code as cidyCode, cit.name as cityName, ar.code as areaCode, ar.name as areaName, lpa.is_default as isDefault from lp_address lpa left join lp_role lpr on lpa.role_id=lpr.id left join province pro on lpa.province_id=pro.id left join city cit on lpa.city_id=cit.id left join area ar on lpa.area_id = ar.id where lpa.is_deleted = 0
and lpa.role_id = #{openId}
and lpa.aid = #{aid}
and lpa.is_default = #{isDefault}
